import { useEffect, useState } from 'react';

const ACTIONS = ['View', 'Reply'];

const formatAsCurrency = (int) => {
  // TODO
  const currency = '$' + int.toFixed(2).replace(/\d(?=(\d{3})+\.)/g, '$&,');
  return currency;
};

const Listing = (props) => {
  // TODO
  // This should be the component which renders an individual listing to the page

  return <div className='listing'>{props.item.title}</div>;
};

const Listings = (props) => {
  // TODO
  // This component should make a request to the api endpoint (props.dataEndpoint)
  // then render the result as set of listings as per the design mocks
  // check props passed in from parent for other values that you may need to use
  const [list, setList] = useState();
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setIsLoading(true);
    fetch(`http://localhost:3000${props.dataEndpoint}`)
      .then((res) => res.json())
      .then((data) => {
        setList(data);
        setIsLoading(false);
      });
  }, []);

  return (
    <div>
      <div className='listings__header'>
              <h1>Search Results</h1>
              <p><span>{list} results</span> for </p>
        </div>
      <div className='listings__grid'>
        {!isLoading ? (
          list &&
          list.map((item) => {
            return <Listing key={item.title} item={item} />;
          })
        ) : (
          <h1>Loading...</h1>
        )}
      </div>
    </div>
  );
};

export default Listings;
